import React, { useState, useEffect, useRef } from 'react';
import './App.css'; // Import the new CSS file

// --- SVG ICONS ---
const BotIcon = ({ className, size = 24 }) => ( <svg className={className} xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"> <path d="M12 8V4H8" /><rect width="16" height="12" x="4" y="8" rx="2" /> <path d="M2 14h2" /><path d="M20 14h2" /><path d="M15 13v2" /><path d="M9 13v2" /> </svg> );
const UserIcon = ({ className, size = 24 }) => ( <svg className={className} xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"> <path d="M19 21v-2a4 4 0 0 0-4-4H9a4 4 0 0 0-4 4v2" /><circle cx="12" cy="7" r="4" /> </svg> );
const ThumbsUpIcon = ({ className, size = 16 }) => ( <svg className={className} xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"> <path d="M7 10v12" /><path d="M15 5.88 14 10h5.83a2 2 0 0 1 1.92 2.56l-2.33 8A2 2 0 0 1 17.5 22H4a2 2 0 0 1-2-2v-8a2 2 0 0 1 2-2h2.76a2 2 0 0 0 1.79-1.11L12 2h0a2 2 0 0 1 1.79 1.11L15 5.88Z" /> </svg> );
const ThumbsDownIcon = ({ className, size = 16 }) => ( <svg className={className} xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"> <path d="M17 14V2" /><path d="M9 18.12 10 14H4.17a2 2 0 0 1-1.92-2.56l2.33-8A2 2 0 0 1 6.5 2H20a2 2 0 0 1 2 2v8a2 2 0 0 1-2 2h-2.76a2 2 0 0 0-1.79 1.11L12 22h0a2 2 0 0 1-1.79-1.11L9 18.12Z" /> </svg> );
const UploadCloudIcon = ({ className, size=48 }) => ( <svg className={className} xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"> <path d="M4 14.899A7 7 0 1 1 15.71 8h1.79a4.5 4.5 0 0 1 2.5 8.242" /><path d="M12 12v9" /><path d="m16 16-4-4-4 4" /> </svg> );
const FileTextIcon = ({ className, size = 20 }) => ( <svg className={className} xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"> <path d="M15 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V7Z" /><path d="M14 2v4a2 2 0 0 0 2 2h4" /> </svg> );
const ScaleIcon = ({ className, size = 20 }) => ( <svg className={className} xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"> <path d="m16 16 3-8 3 8c-2 1-4 1-6 0" /><path d="m2 16 3-8 3 8c-2 1-4 1-6 0" /><path d="M7 21h10" /><path d="M12 3v18" /><path d="M3 7h2" /><path d="M19 7h2" /> </svg> );
const WaypointsIcon = ({ className, size = 20 }) => ( <svg className={className} xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"> <path d="M12 4.5a4.5 4.5 0 1 0 0 9 4.5 4.5 0 0 0 0-9z" /><path d="M5 22h14" /><path d="M5 18h14" /><path d="M5 14h14" /> </svg> );
const SwordsIcon = ({ className, size = 20 }) => ( <svg className={className} xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"> <path d="m14.5 2-2.5 2.5 5 5L19.5 7" /><path d="m9.5 7-5 5 2.5 2.5 5-5" /><path d="M2.5 21.5 7 17" /><path d="m17 7-5 5" /> </svg> );

const API_BASE_URL = 'http://localhost:3001/api';

const ChatbotPage = () => {
    const [messages, setMessages] = useState([
        { id: 1, sender: 'ai', text: 'Hello! I am your AI Law Advisor. How can I assist you with your legal questions today?' }
    ]);
    const [input, setInput] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => { messagesEndRef.current?.scrollIntoView({ behavior: "smooth" }); };
    useEffect(scrollToBottom, [messages]);

    const handleSend = async () => {
        if (input.trim() === '') return;
        setMessages(prev => [...prev, { id: Date.now(), sender: 'user', text: input }]);
        const currentInput = input;
        setInput('');
        setIsTyping(true);
        try {
            const response = await fetch(`${API_BASE_URL}/chat`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ message: currentInput }) });
            const data = await response.json();
            setMessages(prev => [...prev, { id: Date.now() + 1, sender: 'ai', text: data.text, feedback: null }]);
        } catch (error) {
            console.error("Error fetching chat response:", error);
            setMessages(prev => [...prev, { id: Date.now() + 1, sender: 'ai', text: 'Sorry, I am having trouble connecting to my knowledge base. Please try again later.', feedback: null }]);
        } finally {
            setIsTyping(false);
        }
    };

    const handleFeedback = (id, feedback) => { setMessages(msgs => msgs.map(msg => msg.id === id ? { ...msg, feedback } : msg)); };

    return (
        <div className="chatbot-page">
            <div className="chat-messages">
                {messages.map((msg) => (
                    <div key={msg.id} className={`message-container ${msg.sender}`}>
                        {msg.sender === 'ai' && <div className="avatar ai"><BotIcon /></div>}
                        <div className={`message-bubble ${msg.sender}`}>
                             <div className="prose" dangerouslySetInnerHTML={{ __html: msg.text }} />
                            {msg.sender === 'ai' && msg.id > 1 && (
                                <div className="feedback-controls">
                                    <button onClick={() => handleFeedback(msg.id, 'up')} className={`feedback-button ${msg.feedback === 'up' ? 'selected-up' : ''}`}><ThumbsUpIcon /></button>
                                    <button onClick={() => handleFeedback(msg.id, 'down')} className={`feedback-button ${msg.feedback === 'down' ? 'selected-down' : ''}`}><ThumbsDownIcon /></button>
                                </div>
                            )}
                        </div>
                        {msg.sender === 'user' && <div className="avatar user"><UserIcon /></div>}
                    </div>
                ))}
                {isTyping && (
                    <div className="message-container ai">
                        <div className="avatar ai"><BotIcon /></div>
                        <div className="message-bubble ai">
                            <div style={{display: 'flex', gap: '5px', alignItems: 'center'}}>
                                <span style={{width: '8px', height: '8px', backgroundColor: '#94a3b8', borderRadius: '50%', animation: 'pulse 1.4s infinite ease-in-out both'}}></span>
                                <span style={{width: '8px', height: '8px', backgroundColor: '#94a3b8', borderRadius: '50%', animation: 'pulse 1.4s infinite ease-in-out both', animationDelay: '0.2s'}}></span>
                                <span style={{width: '8px', height: '8px', backgroundColor: '#94a3b8', borderRadius: '50%', animation: 'pulse 1.4s infinite ease-in-out both', animationDelay: '0.4s'}}></span>
                            </div>
                        </div>
                    </div>
                )}
                <div ref={messagesEndRef} />
            </div>
            <div className="chat-input-area">
                <div className="chat-input-wrapper">
                    <input type="text" value={input} onChange={(e) => setInput(e.target.value)} onKeyPress={(e) => e.key === 'Enter' && handleSend()} placeholder="Ask a legal question..." className="chat-input"/>
                    <button onClick={handleSend} className="send-button">
                        <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><path d="M5 12h14M12 5l7 7-7 7"/></svg>
                    </button>
                </div>
            </div>
        </div>
    );
};

