const DocumentAnalyzerPage = () => {
    const [file, setFile] = useState(null);
    const [isAnalyzing, setIsAnalyzing] = useState(false);
    const [analysisResult, setAnalysisResult] = useState(null);
    const [error, setError] = useState(null);

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        if (selectedFile) {
            setFile(selectedFile);
            setAnalysisResult(null);
            setError(null);
        }
    };

    const handleAnalyze = async () => {
        if (!file) return;
        setIsAnalyzing(true);
        setAnalysisResult(null);
        setError(null);
        const formData = new FormData();
        formData.append('document', file);

        try {
            const response = await fetch(`${API_BASE_URL}/analyze-document`, {
                method: 'POST',
                body: formData,
            });
            if (!response.ok) throw new Error(`Server error: ${response.statusText}`);
            const data = await response.json();
            setAnalysisResult(data);
        } catch (err) {
            setError('Failed to analyze document. Please try again.');
            console.error(err);
        } finally {
            setIsAnalyzing(false);
        }
    };

    return (
        <Page title="Document Analyzer" subtitle="Upload a legal document to extract key information and generate a summary.">
            <div style={{ maxWidth: '600px', margin: '0 auto' }}>
                <div className="upload-box">
                    <UploadCloudIcon />
                    <h3 style={{marginTop: '0.5rem', fontSize: '0.875rem', fontWeight: 500}}>{file ? `Selected file: ${file.name}` : 'Drag and drop or click to upload'}</h3>
                    <p style={{marginTop: '0.25rem', fontSize: '0.75rem', color: 'var(--text-muted)'}}>PDF, DOCX, TXT up to 10MB</p>
                    <input type="file" onChange={handleFileChange} className="upload-input" accept=".pdf,.docx,.txt" />
                </div>
                <button onClick={handleAnalyze} disabled={!file || isAnalyzing} className="primary-button" style={{ marginTop: '1.5rem' }}>
                    {isAnalyzing ? 'Analyzing...' : 'Analyze Document'}
                </button>
            </div>
            {error && <div className="card" style={{marginTop: '1rem', color: '#dc2626', backgroundColor: '#fee2e2'}}>{error}</div>}
            {analysisResult && (
                 <div className="card" style={{marginTop: '2rem'}}>
                    <h3>Analysis Complete</h3>
                    <pre style={{whiteSpace: 'pre-wrap', wordBreak: 'break-all', maxHeight: '400px', overflowY: 'auto'}}>
                        {JSON.stringify(analysisResult, null, 2)}
                    </pre>
                </div>
            )}
        </Page>
    );
};

const CaseOutcomePredictorPage = () => {
    const [isPredicting, setIsPredicting] = useState(false);
    const [prediction, setPrediction] = useState(null);
    const formRef = useRef();

    const handlePredict = async (e) => {
        e.preventDefault();
        setIsPredicting(true);
        setPrediction(null);
        const formData = new FormData(formRef.current);
        const caseDetails = Object.fromEntries(formData.entries());
        try {
            const response = await fetch(`${API_BASE_URL}/predict-outcome`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(caseDetails)
            });
            const data = await response.json();
            setPrediction(data);
        } catch (error) {
            console.error("Prediction failed:", error);
        } finally {
            setIsPredicting(false);
        }
    };

    return (
        <Page title="Case Outcome Predictor" subtitle="Provide case details to predict the likely outcome.">
            <div className="grid-2-col">
                <form ref={formRef} onSubmit={handlePredict} className="card">
                     <h3 style={{fontWeight: 600, fontSize: '1.125rem'}}>Case Input Form</h3>
                     <div className="form-group"><label>Case Type</label><input name="caseType" type="text" defaultValue="Breach of Contract"/></div>
                     <div className="form-group"><label>Jurisdiction</label><input name="jurisdiction" type="text" defaultValue="California, USA"/></div>
                     <div className="form-group"><label>Summary of Facts</label><textarea name="summary" rows="5" defaultValue="Plaintiff claims defendant failed to deliver goods by the agreed-upon date. Defendant argues unforeseen supply chain issues."></textarea></div>
                      <button type="submit" disabled={isPredicting} className="primary-button">{isPredicting ? 'Calculating...' : 'Predict Outcome'}</button>
                </form>
                <div className="card">
                     <h3 style={{fontWeight: 600, fontSize: '1.125rem'}}>Prediction & Analysis</h3>
                    {!prediction && !isPredicting && <div style={{textAlign: 'center', color: 'var(--text-muted)', paddingTop: '4rem'}}>Results will be displayed here.</div>}
                    {isPredicting && <div style={{textAlign: 'center', color: 'var(--text-muted)', paddingTop: '4rem'}}>Generating prediction...</div>}
                    {prediction && (
                        <div>
                            <div style={{textAlign: 'center'}}>
                                <p style={{fontSize: '0.875rem', color: 'var(--text-muted)'}}>Predicted Outcome</p>
                                <p style={{fontSize: '1.875rem', fontWeight: 'bold', color: 'var(--accent-blue)'}}>{prediction.outcome}</p>
                                <p style={{fontSize: '1.125rem', fontWeight: 500}}>{prediction.probability}% Probability</p>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </Page>
    );
};

