import React, { useState, useEffect, useRef } from 'react';

// --- SVG ICONS ---
const BotIcon = ({ className }) => (
  <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M12 8V4H8" /><rect width="16" height="12" x="4" y="8" rx="2" />
    <path d="M2 14h2" /><path d="M20 14h2" /><path d="M15 13v2" /><path d="M9 13v2" />
  </svg>
);
const UserIcon = ({ className }) => (
  <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M19 21v-2a4 4 0 0 0-4-4H9a4 4 0 0 0-4 4v2" /><circle cx="12" cy="7" r="4" />
  </svg>
);
const ThumbsUpIcon = ({ className }) => (
    <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M7 10v12" /><path d="M15 5.88 14 10h5.83a2 2 0 0 1 1.92 2.56l-2.33 8A2 2 0 0 1 17.5 22H4a2 2 0 0 1-2-2v-8a2 2 0 0 1 2-2h2.76a2 2 0 0 0 1.79-1.11L12 2h0a2 2 0 0 1 1.79 1.11L15 5.88Z" />
    </svg>
);
const ThumbsDownIcon = ({ className }) => (
    <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M17 14V2" /><path d="M9 18.12 10 14H4.17a2 2 0 0 1-1.92-2.56l2.33-8A2 2 0 0 1 6.5 2H20a2 2 0 0 1 2 2v8a2 2 0 0 1-2 2h-2.76a2 2 0 0 0-1.79 1.11L12 22h0a2 2 0 0 1-1.79-1.11L9 18.12Z" />
    </svg>
);
const DownloadIcon = ({ className }) => (
    <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4" /><polyline points="7 10 12 15 17 10" /><line x1="12" x2="12" y1="15" y2="3" />
    </svg>
);
const UploadCloudIcon = ({ className }) => (
    <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M4 14.899A7 7 0 1 1 15.71 8h1.79a4.5 4.5 0 0 1 2.5 8.242" /><path d="M12 12v9" /><path d="m16 16-4-4-4 4" />
    </svg>
);
const FileTextIcon = ({ className }) => (
    <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M15 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V7Z" /><path d="M14 2v4a2 2 0 0 0 2 2h4" />
    </svg>
);
const ScaleIcon = ({ className }) => (
    <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="m16 16 3-8 3 8c-2 1-4 1-6 0" /><path d="m2 16 3-8 3 8c-2 1-4 1-6 0" /><path d="M7 21h10" /><path d="M12 3v18" /><path d="M3 7h2" /><path d="M19 7h2" />
    </svg>
);
const WaypointsIcon = ({ className }) => (
    <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M12 4.5a4.5 4.5 0 1 0 0 9 4.5 4.5 0 0 0 0-9z" /><path d="M5 22h14" /><path d="M5 18h14" /><path d="M5 14h14" />
    </svg>
);
const SwordsIcon = ({ className }) => (
    <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="m14.5 2-2.5 2.5 5 5L19.5 7" /><path d="m9.5 7-5 5 2.5 2.5 5-5" /><path d="M2.5 21.5 7 17" /><path d="m17 7-5 5" />
    </svg>
);

// --- CONFIG ---
const API_BASE_URL = 'http://localhost:3001/api';

// --- HOME / CHATBOT PAGE ---
const ChatbotPage = () => {
    const [messages, setMessages] = useState([
        { id: 1, sender: 'ai', text: 'Hello! I am your AI Law Advisor. How can I assist you with your legal questions today?' }
    ]);
    const [input, setInput] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(scrollToBottom, [messages]);

    const handleSend = async () => {
        if (input.trim() === '') return;
        const newMessages = [...messages, { id: Date.now(), sender: 'user', text: input }];
        setMessages(newMessages);
        const currentInput = input;
        setInput('');
        setIsTyping(true);
        try {
            const response = await fetch(`${API_BASE_URL}/chat`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ message: currentInput })
            });
            const data = await response.json();
            const finalAiMessage = { id: Date.now() + 1, sender: 'ai', text: data.text, feedback: null };
            setMessages(prev => [...prev, finalAiMessage]);
        } catch (error) {
            console.error("Error fetching chat response:", error);
            const errorAiMessage = { id: Date.now() + 1, sender: 'ai', text: 'Sorry, I am having trouble connecting to my knowledge base. Please try again later.', feedback: null };
            setMessages(prev => [...prev, errorAiMessage]);
        } finally {
            setIsTyping(false);
        }
    };

    const handleFeedback = (id, feedback) => {
        setMessages(msgs => msgs.map(msg => msg.id === id ? { ...msg, feedback } : msg));
    };

    return (
        <div className="flex flex-col h-full bg-slate-100">
            <div className="flex-1 overflow-y-auto p-4 md:p-6 space-y-4">
                {messages.map((msg) => (
                    <div key={msg.id} className={`flex items-start gap-4 ${msg.sender === 'user' ? 'justify-end' : ''}`}>
                        {msg.sender === 'ai' && <div className="flex-shrink-0 w-10 h-10 rounded-full bg-slate-700 text-white flex items-center justify-center"><BotIcon className="w-6 h-6" /></div>}
                        <div className={`max-w-xl p-4 rounded-2xl ${msg.sender === 'user' ? 'bg-blue-700 text-white rounded-br-none' : 'bg-white text-slate-800 rounded-bl-none border border-slate-200'}`}>
                             <div className="prose prose-sm max-w-none" dangerouslySetInnerHTML={{ __html: msg.text }} />
                            {msg.sender === 'ai' && msg.id > 1 && (
                                <div className="mt-3 pt-2 border-t border-slate-200 flex items-center gap-2">
                                    <button onClick={() => handleFeedback(msg.id, 'up')} className={`p-1 rounded-full transition-colors ${msg.feedback === 'up' ? 'bg-green-100 text-green-600' : 'text-slate-400 hover:bg-slate-100'}`}><ThumbsUpIcon className="w-4 h-4" /></button>
                                    <button onClick={() => handleFeedback(msg.id, 'down')} className={`p-1 rounded-full transition-colors ${msg.feedback === 'down' ? 'bg-red-100 text-red-600' : 'text-slate-400 hover:bg-slate-100'}`}><ThumbsDownIcon className="w-4 h-4" /></button>
                                </div>
                            )}
                        </div>
                         {msg.sender === 'user' && <div className="flex-shrink-0 w-10 h-10 rounded-full bg-blue-700 text-white flex items-center justify-center"><UserIcon className="w-6 h-6" /></div>}
                    </div>
                ))}
                {isTyping && (
                    <div className="flex items-start gap-4">
                        <div className="flex-shrink-0 w-10 h-10 rounded-full bg-slate-700 text-white flex items-center justify-center"><BotIcon className="w-6 h-6" /></div>
                        <div className="max-w-xl p-4 rounded-2xl bg-white text-slate-800 rounded-bl-none border border-slate-200">
                            <div className="flex items-center gap-2"><span className="w-2 h-2 bg-slate-400 rounded-full animate-pulse delay-0"></span><span className="w-2 h-2 bg-slate-400 rounded-full animate-pulse delay-150"></span><span className="w-2 h-2 bg-slate-400 rounded-full animate-pulse delay-300"></span></div>
                        </div>
                    </div>
                )}
                <div ref={messagesEndRef} />
            </div>
            <div className="p-4 md:p-6 bg-white border-t border-slate-200">
                <div className="relative">
                    <input type="text" value={input} onChange={(e) => setInput(e.target.value)} onKeyPress={(e) => e.key === 'Enter' && handleSend()} placeholder="Ask a legal question..." className="w-full pl-4 pr-12 py-3 rounded-full bg-slate-100 text-slate-800 border border-slate-300 focus:outline-none focus:ring-2 focus:ring-blue-500 transition"/>
                    <button onClick={handleSend} className="absolute right-2 top-1/2 -translate-y-1/2 p-2 rounded-full bg-blue-700 text-white hover:bg-blue-800 transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"><svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 12h14M12 5l7 7-7 7"></path></svg></button>
                </div>
            </div>
        </div>
    );
};

// --- DOCUMENT ANALYZER PAGE ---
const DocumentAnalyzerPage = () => {
    const [file, setFile] = useState(null);
    const [isAnalyzing, setIsAnalyzing] = useState(false);
    const [analysisResult, setAnalysisResult] = useState(null);
    const [error, setError] = useState(null);

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        if (selectedFile) {
            setFile(selectedFile);
            setAnalysisResult(null);
            setError(null);
        }
    };

    const handleAnalyze = async () => {
        if (!file) return;
        setIsAnalyzing(true);
        setAnalysisResult(null);
        setError(null);
        const formData = new FormData();
        formData.append('document', file);
        try {
            const response = await fetch(`${API_BASE_URL}/analyze-document`, { method: 'POST', body: formData });
            if (!response.ok) { throw new Error(`Server error: ${response.statusText}`); }
            const data = await response.json();
            setAnalysisResult(data);
        } catch (err) {
            setError('Failed to analyze document. Please try again.');
            console.error(err);
        } finally {
            setIsAnalyzing(false);
        }
    };

    return (
        <div className="p-6 md:p-8 bg-slate-100 text-slate-800 h-full overflow-y-auto">
            <h2 className="text-2xl font-bold text-slate-800">Document Analyzer</h2>
            <p className="text-slate-500 mt-1">Upload a legal document to extract key information and generate a summary.</p>
            <div className="mt-6 max-w-2xl mx-auto">
                <div className="relative border-2 border-dashed border-slate-300 rounded-xl p-8 text-center bg-white hover:border-blue-500 transition-colors">
                    <div className="mx-auto h-12 w-12 text-slate-400"><UploadCloudIcon /></div>
                    <h3 className="mt-2 text-sm font-medium text-slate-800">{file ? `Selected file: ${file.name}` : 'Drag and drop or click to upload'}</h3>
                    <p className="mt-1 text-xs text-slate-500">PDF, DOCX, TXT up to 10MB</p>
                    <input type="file" onChange={handleFileChange} className="absolute inset-0 w-full h-full opacity-0 cursor-pointer" accept=".pdf,.docx,.txt" />
                </div>
                <button onClick={handleAnalyze} disabled={!file || isAnalyzing} className="mt-6 w-full py-3 px-4 bg-blue-700 text-white font-semibold rounded-lg shadow-md hover:bg-blue-800 disabled:bg-slate-400 disabled:cursor-not-allowed transition-all flex items-center justify-center">
                    {isAnalyzing ? (<><svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path></svg>Analyzing Document...</>) : 'Analyze Document'}
                </button>
            </div>
            {error && <div className="mt-4 text-center text-red-600 bg-red-100 p-3 rounded-md">{error}</div>}
            {analysisResult && (
                <div className="mt-8 bg-white border border-slate-200 rounded-xl p-6">
                    <div className="flex justify-between items-center"><h3 className="text-xl font-bold text-slate-800">Analysis Results</h3><button className="flex items-center gap-2 text-sm bg-slate-200 hover:bg-slate-300 text-slate-700 font-medium py-2 px-3 rounded-md transition"><DownloadIcon className="w-4 h-4"/>Export as JSON</button></div>
                    <div className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="space-y-4"><h4 className="font-semibold text-slate-800">Key Metrics</h4><ul className="space-y-2 text-sm">{Object.entries(analysisResult.metrics).map(([key, value]) => (<li key={key} className="flex"><span className="font-medium text-slate-500 w-32 flex-shrink-0">{key}:</span><span className="text-slate-800">{value}</span></li>))}</ul></div>
                        <div className="space-y-4"><h4 className="font-semibold text-slate-800">Generated Summary</h4><ul className="list-disc list-inside space-y-2 text-sm text-slate-800">{analysisResult.summary.map((item, index) => <li key={index}>{item}</li>)}</ul></div>
                    </div>
                    <div className="mt-6"><h4 className="font-semibold text-slate-800">Clause & Argument Highlights</h4><div className="mt-4 space-y-3">{analysisResult.highlights.map((item, index) => (<div key={index} className="p-3 bg-slate-50 rounded-md border-l-4 border-blue-500"><span className={`text-xs font-bold uppercase ${item.type === 'Clause' ? 'text-blue-700' : item.type === 'Argument' ? 'text-purple-700' : 'text-amber-700'}`}>{item.type}</span><p className="mt-1 text-sm text-slate-800">{item.text}</p></div>))}</div></div>
                </div>
            )}
        </div>
    );
};

// --- CASE OUTCOME PREDICTOR ---
const CaseOutcomePredictorPage = () => {
    const [isPredicting, setIsPredicting] = useState(false);
    const [prediction, setPrediction] = useState(null);
    const formRef = useRef();

    const handlePredict = async (e) => {
        e.preventDefault();
        setIsPredicting(true);
        setPrediction(null);
        const formData = new FormData(formRef.current);
        const caseDetails = Object.fromEntries(formData.entries());
        try {
            const response = await fetch(`${API_BASE_URL}/predict-outcome`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(caseDetails) });
            const data = await response.json();
            setPrediction(data);
        } catch (error) {
            console.error("Prediction failed:", error);
        } finally {
            setIsPredicting(false);
        }
    };

    return (
         <div className="p-6 md:p-8 bg-slate-100 text-slate-800 h-full overflow-y-auto">
            <h2 className="text-2xl font-bold text-slate-800">Case Outcome Predictor</h2>
            <p className="text-slate-500 mt-1">Provide case details to predict the likely outcome.</p>
            <div className="mt-6 grid grid-cols-1 lg:grid-cols-2 gap-8">
                <form ref={formRef} onSubmit={handlePredict} className="bg-white p-6 rounded-xl border border-slate-200 space-y-4">
                     <h3 className="font-semibold text-lg text-slate-800">Case Input Form</h3>
                     <div><label className="text-sm font-medium text-slate-800">Case Type</label><input name="caseType" type="text" defaultValue="Breach of Contract" className="mt-1 w-full p-2 border border-slate-300 rounded-md bg-slate-50 text-slate-800"/></div>
                     <div><label className="text-sm font-medium text-slate-800">Jurisdiction</label><input name="jurisdiction" type="text" defaultValue="California, USA" className="mt-1 w-full p-2 border border-slate-300 rounded-md bg-slate-50 text-slate-800"/></div>
                     <div><label className="text-sm font-medium text-slate-800">Summary of Facts</label><textarea name="summary" rows="5" defaultValue="Plaintiff claims defendant failed to deliver goods by the agreed-upon date. Defendant argues unforeseen supply chain issues." className="mt-1 w-full p-2 border border-slate-300 rounded-md bg-slate-50 text-slate-800"></textarea></div>
                      <button type="submit" disabled={isPredicting} className="w-full py-3 px-4 bg-blue-700 text-white font-semibold rounded-lg shadow-md hover:bg-blue-800 disabled:bg-slate-400 disabled:cursor-not-allowed transition-all flex items-center justify-center">{isPredicting ? 'Calculating...' : 'Predict Outcome'}</button>
                </form>
                <div className="bg-white p-6 rounded-xl border border-slate-200">
                    <h3 className="font-semibold text-lg text-slate-800">Prediction & Analysis</h3>
                    {!prediction && !isPredicting && <div className="text-center text-slate-500 pt-16">Results will be displayed here.</div>}
                    {isPredicting && <div className="text-center text-slate-500 pt-16">Generating prediction...</div>}
                    {prediction && (
                        <div className="space-y-6">
                            <div className="text-center"><p className="text-sm text-slate-500">Predicted Outcome</p><p className="text-3xl font-bold text-blue-700">{prediction.outcome}</p><p className="text-lg font-medium text-slate-800">{prediction.probability}% Probability</p><p className="text-xs text-slate-400">AI Confidence Score: {prediction.confidence}%</p></div>
                            <div><h4 className="font-semibold text-slate-800">Reasoning</h4><p className="text-sm text-slate-500 mt-2">{prediction.reasoning}</p></div>
                            <div><h4 className="font-semibold text-slate-800">Influential Factors</h4><div className="mt-2 space-y-2">{prediction.factors.map(factor => (<div key={factor.name}><div className="flex justify-between text-sm mb-1"><span>{factor.name}</span><span className={`${factor.type === 'positive' ? 'text-green-600' : 'text-red-600'}`}>{factor.type === 'positive' ? '+' : '-'} Impact</span></div><div className="w-full bg-slate-200 rounded-full h-2.5"><div className={`${factor.type === 'positive' ? 'bg-green-500' : 'bg-red-500'} h-2.5 rounded-full`} style={{width: `${factor.impact * 100}%`}}></div></div></div>))}</div></div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

// --- CASE FLOW VISUALIZATION ---
const CaseFlowVisualizerPage = () => {
    const [activeNode, setActiveNode] = useState('Complaint Filing');
    const nodes = [ { id: 'Complaint Filing', title: 'Complaint Filing', description: 'The case begins when the plaintiff files a complaint with the appropriate court, outlining the grievances against the defendant.'}, { id: 'Discovery', title: 'Discovery', description: 'Both parties exchange information, evidence, and documents. This includes depositions, interrogatories, and requests for documents.'}, { id: 'Pre-Trial Motions', title: 'Pre-Trial Motions', description: 'Parties may file motions to resolve the case before trial, such as a motion to dismiss or a motion for summary judgment.'}, { id: 'Trial', title: 'Trial', description: 'If the case is not settled or dismissed, it proceeds to trial where evidence is presented and arguments are made before a judge or jury.'}, { id: 'Verdict & Judgment', title: 'Verdict & Judgment', description: 'The judge or jury delivers a verdict. The court then issues a final judgment, officially ending the case at the trial level.'}, { id: 'Appeal', title: 'Appeal', description: 'The losing party may appeal the decision to a higher court, arguing that a legal error occurred during the trial.'}, ];
    const activeNodeData = nodes.find(n => n.id === activeNode);
    return (
        <div className="p-6 md:p-8 bg-slate-100 text-slate-800 h-full overflow-y-auto">
            <h2 className="text-2xl font-bold text-slate-800">Case Flow Visualizer</h2>
            <p className="text-slate-500 mt-1">An interactive timeline of a typical legal case progression.</p>
            <div className="mt-12">
                <div className="relative"><div className="absolute left-0 right-0 top-1/2 -translate-y-1/2 h-1 bg-slate-300"></div><div className="relative flex justify-between">{nodes.map(node => (<div key={node.id} className="z-10 text-center"><button onClick={() => setActiveNode(node.id)} className={`w-6 h-6 rounded-full flex items-center justify-center mx-auto transition-all duration-300 ${activeNode === node.id ? 'bg-blue-700 scale-125 ring-4 ring-white' : 'bg-slate-400 hover:bg-blue-700'}`}></button><p className={`mt-2 text-xs md:text-sm font-medium transition-colors ${activeNode === node.id ? 'text-blue-700' : 'text-slate-500'}`}>{node.title}</p></div>))}</div></div>
                <div className="mt-10 max-w-3xl mx-auto bg-white p-6 rounded-xl border border-slate-200 shadow-sm"><h3 className="text-xl font-bold text-slate-800">{activeNodeData.title}</h3><p className="mt-2 text-slate-800">{activeNodeData.description}</p><div className="mt-4"><button className="flex items-center gap-2 text-sm text-blue-700 hover:underline"><FileTextIcon className="w-4 h-4" />Link Analyzed Documents</button></div></div>
            </div>
        </div>
    );
};

// --- OPPOSITION ARGUMENTS GENERATOR ---
const OppositionArgumentsGeneratorPage = () => {
    const [isGenerating, setIsGenerating] = useState(false);
    const [results, setResults] = useState(null);
    const formRef = useRef();
    const handleGenerate = async (e) => {
        e.preventDefault();
        setIsGenerating(true);
        setResults(null);
        const formData = new FormData(formRef.current);
        const caseDetails = Object.fromEntries(formData.entries());
        try {
            const response = await fetch(`${API_BASE_URL}/generate-arguments`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(caseDetails) });
            const data = await response.json();
            setResults(data);
        } catch (error) {
            console.error("Failed to generate arguments:", error);
        } finally {
            setIsGenerating(false);
        }
    }
    return (
        <div className="p-6 md:p-8 bg-slate-100 text-slate-800 h-full overflow-y-auto">
            <h2 className="text-2xl font-bold text-slate-800">Opposition Arguments Generator</h2>
            <p className="text-slate-500 mt-1">Generate potential counterarguments your opposition might raise.</p>
            <div className="mt-6">
                <form ref={formRef} onSubmit={handleGenerate} className="max-w-3xl mx-auto bg-white p-6 rounded-xl border border-slate-200 space-y-4">
                     <h3 className="font-semibold text-lg text-slate-800">Your Case Details</h3>
                     <div><label className="text-sm font-medium text-slate-800">Case Type</label><input name="caseType" type="text" defaultValue="Breach of Contract" className="mt-1 w-full p-2 border border-slate-300 rounded-md bg-slate-50 text-slate-800"/></div>
                     <div><label className="text-sm font-medium text-slate-800">Your Core Argument</label><textarea name="coreArgument" rows="4" defaultValue="The defendant failed to meet the contractually mandated delivery deadline, causing financial harm to our business." className="mt-1 w-full p-2 border border-slate-300 rounded-md bg-slate-50 text-slate-800"></textarea></div>
                     <button type="submit" disabled={isGenerating} className="w-full py-3 px-4 bg-blue-700 text-white font-semibold rounded-lg shadow-md hover:bg-blue-800 disabled:bg-slate-400 disabled:cursor-not-allowed transition-all flex items-center justify-center">{isGenerating ? 'Generating...' : 'Generate Counterarguments'}</button>
                </form>
                {results && (
                    <div className="mt-8 max-w-4xl mx-auto">
                        <h3 className="text-xl font-bold text-center text-slate-800">Generated Counterarguments</h3>
                        <div className="mt-4 space-y-4">{results.map((item, index) => (<div key={index} className="bg-white border border-slate-200 rounded-xl p-5"><h4 className="font-bold text-blue-700">{index + 1}. {item.argument}</h4><div className="mt-4 grid grid-cols-1 md:grid-cols-2 gap-4 text-sm"><div><p className="font-semibold text-slate-800 mb-1">Strength & Weakness Analysis</p><p className="text-slate-500">{item.analysis}</p></div><div><p className="font-semibold text-slate-800 mb-1">Suggested Response Strategy</p><p className="text-slate-500">{item.response}</p></div></div></div>))}</div>
                    </div>
                )}
            </div>
        </div>
    );
};

// --- MAIN APP COMPONENT ---
export default function App() {
    const [currentPage, setCurrentPage] = useState('chatbot');
    const navItems = [ { id: 'chatbot', label: 'Law Advisor Chat', icon: <BotIcon className="w-5 h-5" /> }, { id: 'analyzer', label: 'Document Analyzer', icon: <FileTextIcon className="w-5 h-5" /> }, { id: 'predictor', label: 'Case Predictor', icon: <ScaleIcon className="w-5 h-5" /> }, { id: 'visualizer', label: 'Case Flow Visualizer', icon: <WaypointsIcon className="w-5 h-5" /> }, { id: 'arguments', label: 'Arguments Generator', icon: <SwordsIcon className="w-5 h-5" /> }, ];
    const renderPage = () => {
        switch (currentPage) {
            case 'chatbot': return <ChatbotPage />;
            case 'analyzer': return <DocumentAnalyzerPage />;
            case 'predictor': return <CaseOutcomePredictorPage />;
            case 'visualizer': return <CaseFlowVisualizerPage />;
            case 'arguments': return <OppositionArgumentsGeneratorPage />;
            default: return <ChatbotPage />;
        }
    };
    return (
        <div className="h-screen w-screen bg-slate-100 font-sans text-slate-800 flex">
            <aside className="w-64 bg-slate-900 border-r border-slate-800 flex flex-col flex-shrink-0">
                <div className="h-16 flex items-center px-6 border-b border-slate-800"><ScaleIcon className="w-8 h-8 text-amber-400" /><h1 className="ml-3 text-xl font-bold text-slate-100">Judicio</h1></div>
                <nav className="flex-1 p-4 space-y-2">{navItems.map(item => (<button key={item.id} onClick={() => setCurrentPage(item.id)} className={`w-full flex items-center gap-3 px-4 py-2.5 rounded-md text-sm font-medium transition-colors ${currentPage === item.id ? 'bg-slate-700 text-white' : 'text-slate-300 hover:bg-slate-800'}`}>{item.icon}<span>{item.label}</span></button>))}</nav>
            </aside>
            <main className="flex-1 h-full">{renderPage()}</main>
        </div>
    );
}
